$(document).ready(function () {
  $("#fullpage").fullpage({
    anchors: ["home", "quienSoy", "trabajos", "porta", "contacto"],
    navigation: true,
    navigationPosition: "right",
    navigationTooltips: [
      "Home",
      "Quien soy",
      "Trabajos",
      "Portafolio",
      "Contacto",
    ],
    showActiveTooltip: false,
    dragAndMove: "fingersonly",
    scrollingSpeed: 1200,
    css3: true,
    easingcss3: "cubic-bezier(1.000, -0.205, 0.615, 1.000)",
    responsiveWidth: 640,
    responsiveSlides: true,
  });
  $("#elMenu").hide();
  $("#barrasc").hide();
  $("#barrasl").click(function () {
    $(this).fadeOut(300);
    $("#elMenu").fadeToggle("slow", function () {
      $("#barrasc").fadeIn(300);
    });
  });
  $("#barrasc").click(function () {
    $(this).fadeOut(300);
    $("#elMenu").fadeToggle("slow", function () {
      $("#barrasl").fadeIn(300);
    });
  });
  $("nav ul li a, #fp-nav ul li a, .fp-controlArrow, #elMenu").click(
    function () {
      $("#elMenu").delay(100).fadeOut("slow");
      $("#barrasc").fadeOut(function () {
        $("#barrasl").fadeIn(300);
      });
    }
  );
  lightbox.option({
    resizeDuration: 300,
    wrapAround: true,
    albumLabel: "",
    alwaysShowNavOnTouchDevices: true,
  });
});
